import React, { useState } from 'react';
import './toggle.css'

const Toggle = ({ label, toggled, onClick }) => {
    const [isToggled, toggle] = useState(toggled);

    const callback = () => {
        toggle(!isToggled)
        onClick(!isToggled)
    }
  return (
    <div className=''>
    <label>
        <input type="checkbox" defaultChecked={isToggled} onClick={callback} />
        <span />
        <strong>{label}</strong>
    </label>
    </div>
  )
}

export default Toggle
