import React from 'react'
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import Toggle from './Toggle';
import Volume from './Volume';
import Heater_1 from './Assets/Heater-1.mp3'
import Heater_2 from './Assets/Heater-2.mp3'
import Heater_3 from './Assets/Heater-3.mp3'
import Heater_4 from './Assets/Heater-4_1.mp3'
import clap from './Assets/Heater-6.mp3'
import open_hh from './Assets/Dsc_Oh.mp3'
import Kick_n_Hat from './Assets/Kick_n_Hat.mp3'
import kick from './Assets/RP4_KICK_1.mp3'
import closed_hh from './Assets/Cev_H2.mp3'

// import { useState, useEffect } from 'react'
const test = () =>{
  console.log("It is working");
}

const Body = () =>{
  const drumPads = [
    {
      keyCode: 81,
      text: "Q",
      src: Heater_1
    },
    {
      keyCode: 87,
      text: "W",
      src: Heater_2
    },
    {
      keyCode: 69,
      text: "E",
      src: Heater_3
    },
    {
      keyCode: 65,
      text: "A",
      src: Heater_4
    },
    {
      keyCode: 83,
      text: "S",
      src: clap
    },
    {
      keyCode: 68,
      text: "D",
      src: open_hh
    },
    {
      keyCode: 90,
      text: "Z",
      src: Kick_n_Hat
    },
    {
      keyCode: 88,
      text: "X",
      src: kick
    },
    {
      keyCode: 67,
      text: "C",
      src: closed_hh
    }
  ];




  return(
    <div className='container'>
      <div id='drum-machine'>
          {/* Creating the drum kit */}
          <div className='drum-kit'>
            {drumPads.map((drumPad) => <div className='drum-pad' id={drumPad.text}>{drumPad.text}
