            </button>


            <button className='drum-pad btn' id='heater-2' onClick={() =>{
              new Audio(Heater_2).play();
            }}>
              <p>W</p>
              <audio src={Heater_2} id='W' className='clip'></audio>
            </button>

            <button className='drum-pad btn' id='heater-3' onClick={() =>{
              new Audio(Heater_3).play()
            }}>
              <p>E</p>
              <audio src={Heater_3} id='E' className='clip'></audio>
            </button>


            <button className='drum-pad btn' id='heater-4' onClick={() =>{
              new Audio(Heater_4).play();
            }}> 
              <p>A</p>
              <audio src={Heater_4} id='A' className='clip'></audio>
            </button>


            <button className='drum-pad btn' id='clap' onClick={() =>{
              new Audio(clap).play();
            }}>
              <p>S</p>
              <audio src={clap} id='S' className='clip'></audio>
            </button>


            <button className='drum-pad btn' id='open-hh' onClick={() =>{
              new Audio(open_hh).play();
            }}>
              <p>D</p>
              <audio src={open_hh} id='D' className='clip'></audio>
            </button>


            <button className='drum-pad btn' id='kick-n-hat' onClick={() =>{
              new Audio(Kick_n_Hat).play();
            }}>
              <p>Z</p>
              <audio src={Kick_n_Hat} id='Z' className='clip'></audio>
            </button>


            <button className='drum-pad btn' id='kick' onClick={() =>{
              new Audio(kick).play();
            }}>
              <p>X</p>
              <audio src={kick} id='X' className='clip'></audio>
            </button>



            <button className='drum-pad btn' id='closed-hh' onClick={() =>{
              new Audio(closed_hh).play();
            }}>
              <p>C</p>
              <audio src={closed_hh} id='C' className='clip'></audio>
            </button> */}
          </div>

          {/* Creating the two toggle buttons, the screen and, the volume button*/}

          {/* power button */}
          <div className='toggle1'>
            <Toggle label="Power" toggled={true} onClick={test} />
          </div>

          {/* screen display */}
          <div id='display'>
            <p>volume: 55</p>
          </div>

          {/* volume slider */}
          <div className='volume-btn'>
            <Volume/>
          </div>


          {/* Bank button */}
          <div className='toggle2'>
            <Toggle label="Bank" toggled={false} onClick={test} />
          </div>


        </div>
      </div>
  )
}

const App = () => {
  return (
    <div>
      <Body />
    </div>
  )
}

export default App
