import React, { useState } from 'react';
import './toggle.css'

const Toggle = ({ label, toggled}) => {
    const [isToggled, toggle] = useState(toggled);

    const callback = () => {
        toggle(!isToggled)
        onClick(!isToggled)
    }
  return (
    <label>
        <input type="checkbox" defaultChecked={isToggled} onClick={callback}/>
        <span />
        <strong>{label}</strong>
    </label>

  )
}

export default Toggle
